import React, { createContext, useContext } from 'react'
import { useState } from 'react'
import Table from 'react-bootstrap/Table'
import Axios from 'axios'
import { useEffect } from 'react'
import UserRecord from './UserRecord'
import { UserDetailsContext } from './userDetailsContext'
import { allUsersContext, AllUsersContextProvider } from './AllUsersContext'


const UsersPage = () => {

    const UserListContext = createContext()
    //context to make the users accessible
    const userContext = useContext(allUsersContext);
    //state to hold the users in an array
    //const [users, setUsers] = useState([]);

    const getUsers = () => {
        Axios.get('http://localhost:3001/fetchusers').then((response) => {
            setUsers(response.data);
        })
    }

    //gets the userdata from the database
    useEffect(() => {
        getUsers()
        console.log("Rendered")
    }, [])

    return (
        <div className='vh-100 w-auto d-flex justify-content-center align-items-start pt-5 bg-dark'>
            <div className='w-50 d-flex justify-content-center m-0 rounded-5 bg-light'>
                <UserListContext.Provider value={{users, setUsers}}>
                    <Table striped hover>
                        <thead>
                            <tr>
                                <th>#</th>
                                <th>Name</th>
                                <th>Address</th>
                                <th>Bill</th>
                                <th>UpToDate</th>
                            </tr>
                        </thead>
                        <tbody>
                            {/* shows the users */}
                            {users.map((userData) => {

                                return (
                                    <UserDetailsContext.Provider
                                        key={userData.idBillingList}
                                        value={userData}>

                                        <UserRecord
                                            key={userData.idBillingList}
                                        //showModal={showModal}
                                        //setShowModal={setShowModal}
                                        //handleEditOpen={handleEditOpen}
                                        //handleEditClose={handleEditClose}
                                        />

                                    </UserDetailsContext.Provider>
                                )
                            })}
                        </tbody>
                    </Table>
                </UserListContext.Provider>
            </div>
        </div>

    )
}

export default UsersPage
