import React from 'react'
import Button from 'react-bootstrap/Button'
import EditRecordComp from './EditRecordComp';
import { useState } from 'react';


{/* holds the data for each user */ }
const UserRecord = (props) => {
    const [showModal, setShowModal] = useState(false);

    return (

        <tr>
            {/*conditional rendering the modal*/}
            {showModal &&
            <EditRecordComp
                showModal={showModal}
                userDetails={userDetails}
            />}

            <td>{props.userDetails.userID}</td>
            <td>{props.userDetails.userName}</td>
            <td>{props.userDetails.userAddress}</td>
            <td>{props.userDetails.userBill}</td>
            <td>{props.userDetails.userPaidLastMonth === 1 ? "Yes" : "No"}</td>
            <td>
                <Button onClick={props.handleEditOpen}>Edit</Button>
            </td>
        </tr>
    )
}

export default UserRecord
