import React from 'react'
import Button from 'react-bootstrap/Button'
import EditRecordComp from './EditRecordComp';
import { useState } from 'react';


{/* holds the data for each user */ }
const UserRecord = (props) => {
    const [showModal, setShowModal] = useState(false);
    const userDetails = 
        {
            userID: props.idBillingList,
            userName: props.Name,
            userAddress: props.userAddress
            }

    return (

        <tr>
            {/*conditional rendering the modal*/}
            {showModal &&
            <EditRecordComp
                showModal={showModal}
                
            />}

            <td>{props.userDetails.idBillingList}</td>
            <td>{props.userDetails.Name}</td>
            <td>{props.userDetails.Address}</td>
            <td>{props.userDetails.Bill}</td>
            <td>{props.userDetails.PayedLastMonth === 1 ? "Yes" : "No"}</td>
            <td>
                <Button onClick={props.handleEditOpen}>Edit</Button>
            </td>
        </tr>
    )
}

export default UserRecord
