import React from 'react'
import Button from 'react-bootstrap/Button'
import EditRecordComp from './EditRecordComp';
import { useState, useContext } from 'react';
import { UserDetailsContext } from './userDetailsContext';


{/* holds the data for each user */ }
const UserRecord = (props) => {
    const [showModal, setShowModal] = useState(false);
    const userDetails = useContext(UserDetailsContext)

    return (

        <tr>
            {/*conditional rendering the modal*/}
            {showModal &&
            <EditRecordComp
                showModal={showModal}
                currentUser="teet"
            />}

            <td>{userDetails.idBillingList}</td>
            <td>{userDetails.userName}</td>
            <td>{userDetails.userAddress}</td>
            <td>{userDetails.userBill}</td>
            <td>{userDetails.userPaidLastMonth === 1 ? "Yes" : "No"}</td>
            <td>
                <Button onClick={props.handleEditOpen}>Edit</Button>
            </td>
        </tr>
    )
}

export default UserRecord
