import React from 'react'
import { useState } from 'react'
import Table from 'react-bootstrap/Table'
import Axios from 'axios'
import { useEffect } from 'react'


const UsersPage = () => {
    const [users, setUsers] = useState([]);

    const getUsers = () => {
        Axios.get('http://localhost:3001/fetchusers').then((response) => {
            setUsers(response.data);
        })
    }

    //gets the userdata from the database
    useEffect(() => {
        getUsers()
        console.log("Rendered")
    }, [])

    return (
        <div className='UsersPageContainer h-auto w-auto d-flex justify-content-center align-items-center'>
            <div className='w-50 d-flex justify-content-center m-0'>
                <Table striped border hover>
                    <thead>
                        <tr>
                            <th>#</th>
                            <th>Name</th>
                            <th>Address</th>
                            <th>Bill</th>
                            <th>UpToDate</th>
                        </tr>
                    </thead>
                    <tbody>
                        {users.map((userData) => {
                            return <tr key={userData.idBillingList}>
                                <td>{userData.idBillingList}</td>
                                <td>{userData.Name}</td>
                                <td>{userData.Address}</td>
                                <td>{userData.Bill}</td>
                                <td>{userData.PayedLastMonth == 1 ? "Yes" : "No"}</td>
                            </tr>
                        })}
                    </tbody>
                </Table>
            </div>
        </div>

    )
}

export default UsersPage
